const metadataPrefix = 'https://gateway.pinata.cloud/ipfs/QmY6Dt2tu9THDQiHo4BkquwgvUZRgPYJBg9urvSNyBmaVo/';
const metadataExt = '.png';

export const DelistModal = ({ showModal, 
                            setShowModal, 
                            nftImg,
                            isLoading,
                            delistInvader }) => {

  const modalRef = useRef();

  const animation = useSpring({
    config: {
      duration: 250
    },
    opacity: showModal ? 1 : 0,
    transform: showModal ? `translateY(0%)` : `translateY(-100%)`
  });

  const closeModal = e => {
    if (modalRef.current === e.target) {
      setShowModal(false);
    }
  };

  const keyPress = useCallback(
    e => {
      if (e.key === 'Escape' && showModal) {
        setShowModal(false);
        console.log('I pressed');
      }
    },
    [setShowModal, showModal]
  );

  useEffect(
    () => {
      document.addEventListener('keydown', keyPress);
      return () => document.removeEventListener('keydown', keyPress);
    },
    [keyPress]
  );

  return (
    <>
      {showModal ? (
          <Background onClick={closeModal} ref={modalRef}>
              <animated.div style={animation}>
                <ModalWrapper showModal={showModal}>
                <Image
                    preview={false}
                    src={nftImg || "error"}
                    fallback="data:image/png;base64,"
                    alt=""
                    style={{ width: '100px' }}
                />
                <ModalContent>
                    <h1></h1>
                    <p>To delist this Invader, click "Delist" below.</p>  
                    <SpinnerDotted 
                        enabled={isLoading} 
                        color={'#6C63FF'} 
                        style={{marginTop:'15px'}} 
                        size={35}
                        />         
                    <button onClick={delistInvader} 
                            disabled={isLoading}
                            style={{background: isLoading ? '#B1B1B1' : '#6C63FF', 
                                    cursor: isLoading ? 'default' : 'pointer',
                                    marginTop: '10px'
                                    }}
                            >
                                Delist
                    </button>
                    
                </ModalContent>
                
                <CloseModalButton
                    aria-label='Close modal'
                    onClick={() => {
                        setShowModal(prev => !prev);
                    }}
                />
                </ModalWrapper>
              </animated.div>
            </Background>  
      ) : null}
    </>
  );
};